#Defszinhas
def clear():
	if platform.system() == "Windows":
		os.system("cls")
	else:
		os.system("clear")
def centraliza_texto(txt):
	centrao = os.get_terminal_size().columns
	return txt.center(centrao)
def opc1(numero, txt):
	print (f'{texto.negrito}{texto.fundo.rosa}{texto.cor.branco} [{numero}] {texto.fundo.preto} {texto.cor.rosa_claro}{txt} {texto.reseta}')
def opc2(numero, txt):
	print (f'{texto.negrito}{texto.fundo.vermelho}{texto.cor.branco} [{numero}] {texto.fundo.preto} {texto.cor.vermelho_claro}{txt} {texto.reseta}')
def erro(txt):
	tamanho = len(txt)+2
	print ('\u001b[38;5;220m┌'+'\u001b[38;5;220m─'*tamanho+'\u001b[38;5;220m┐'+'\n'+'\u001b[38;5;220m│ '+'\u001b[38;5;196m'+txt+'\u001b[0m'+' \u001b[38;5;220m│'+'\n'+'\u001b[38;5;220m└'+'\u001b[38;5;220m─'*tamanho+'\u001b[38;5;220m┘\u001b[0m')
def sucesso(txt):
	tamanho = len(txt)+2
	print ('\u001b[38;5;85m┌'+'\u001b[38;5;85m─'*tamanho+'\u001b[38;5;85m┐'+'\n'+'\u001b[38;5;85m│ '+'\u001b[38;5;40m'+txt+'\u001b[0m'+' \u001b[38;5;85m│'+'\n'+'\u001b[38;5;85m└'+'\u001b[38;5;85m─'*tamanho+'\u001b[38;5;85m┘\u001b[0m')
def log(txt):
	print (f'{texto.italico} {texto.cor.branco}[{texto.cor.amarelo_claro} {txt} {texto.cor.branco}]{texto.reseta}')
def titulo(txt):
	print(f'{texto.cor.rosa_claro}{texto.negrito}{texto.cor.branco}- {texto.cor.rosa_claro}{txt} {texto.cor.branco}-{texto.reseta}')

